mapper.select:
SELECT IT.*, ITI.*
FROM O_ITEM_NEW IT
	LEFT JOIN ITEMINFO ITI ON IT.ITEM_CD = ITI.ITI_ITEMCD

	AND ITEM_CD = #{r_itemcd}
	AND ITEM_CD LIKE '%' + #{rl_itemcd} + '%'
	AND SALES_CD1_NM = #{r_salescd1nm}
	AND SALES_CD2_NM = #{r_salescd2nm}
	AND SALES_CD3_NM = #{r_salescd3nm}
	AND SALES_CD4_NM = #{r_salescd4nm}
	AND DESC1 = #{r_desc1}
	AND DESC1 LIKE '%' + #{rl_desc1} + '%'
	AND DESC2 = #{r_desc2}
	AND DESC2 LIKE '%' + #{rl_desc2} + '%'
	AND SEARCH_TEXT LIKE '%' + #{rl_searchtext} + '%'
	AND THICK_NM LIKE '%' + #{rl_thicknm} + '%'
	AND WIDTH_NM LIKE '%' + #{rl_widthnm} + '%'
	AND LENGTH_NM LIKE '%' + #{rl_lengthnm} + '%'

		AND (
			ITI_FILE1 IS NULL OR ISNULL(ITI_FILE1, ' ') = ' '

		)


		AND 0 >= (SELECT COUNT(*) FROM ITEMRECOMMEND WHERE ITR_ITEMCD = IT.ITEM_CD)


		AND ITEM_CD IN ( SELECT DISTINCT(ITS_ITEMCD) FROM ITEMSEARCH WHERE  (ITS_TEXT LIKE #{"its_text"} + '%') )

SELECT CT.* FROM(

SELECT ${r_groupby} AS CATEGORY_NAME

FROM O_ITEM_NEW

	AND SALES_CD1_NM = #{r_salescd1nm}
	AND SALES_CD2_NM = #{r_salescd2nm}
	AND SALES_CD3_NM = #{r_salescd3nm}
	AND SALES_CD4_NM = #{r_salescd4nm}


GROUP BY ${r_groupby}
) CT

	ISNULL(CT.CATEGORY_NAME, ' ') != ' '


ORDER BY CT.CATEGORY_NAME ASC

mapper.update:
MERGE INTO ITEMINFO

USING (SELECT 1 AS dual) AS D ON (ITI_ITEMCD = #{m_itiitemcd})
WHEN MATCHED THEN 
	UPDATE SET 
		ITI_PALLET = #{m_itipallet}

		, ITI_SEARCHWORD = #{m_itisearchword}

		, ITI_LINK = #{m_itilink}

			, ITI_FILE1 = #{m_itifile1}
			, ITI_FILE1TYPE = #{m_itifile1type}


			, ITI_FILE2 = #{m_itifile2}
			, ITI_FILE2TYPE = #{m_itifile2type}


			, ITI_FILE3 = #{m_itifile3}
			, ITI_FILE3TYPE = #{m_itifile3type}


			, ITI_FILE4 = #{m_itifile4}
			, ITI_FILE4TYPE = #{m_itifile4type}


			, ITI_FILE5 = #{m_itifile5}
			, ITI_FILE5TYPE = #{m_itifile5type}

		, ITI_SORT = #{m_itisort}
		, ITI_MOID = #{m_itiinid}
		, ITI_MODATE = GETDATE()
WHEN NOT MATCHED THEN
	INSERT (
		ITI_ITEMCD
		, ITI_PALLET
		, ITI_SEARCHWORD
		, ITI_LINK
		, ITI_FILE1
		, ITI_FILE1TYPE
		, ITI_FILE2
		, ITI_FILE2TYPE
		, ITI_FILE3
		, ITI_FILE3TYPE
		, ITI_FILE4
		, ITI_FILE4TYPE
		, ITI_FILE5
		, ITI_FILE5TYPE
		, ITI_SORT
		, ITI_INID
		, ITI_INDATE
	)VALUES(
		#{m_itiitemcd}
		, #{m_itipallet}
		, #{m_itisearchword}
		, #{m_itilink}
		, #{m_itifile1}
		, #{m_itifile1type}
		, #{m_itifile2}
		, #{m_itifile2type}
		, #{m_itifile3}
		, #{m_itifile3type}
		, #{m_itifile4}
		, #{m_itifile4type}
		, #{m_itifile5}
		, #{m_itifile5type}
		, #{m_itisort}
		, #{m_itiinid}
		, GETDATE()
	) ;

UPDATE ITEMINFO ITI
ITI_FILE1 = NULL, ITI_FILE1TYPE = NULL, ITI_FILE2 = NULL, ITI_FILE2TYPE = NULL, ITI_FILE3 = NULL, ITI_FILE3TYPE = NULL, ITI_FILE4 = NULL, ITI_FILE4TYPE = NULL, ITI_FILE5 = NULL, ITI_FILE5TYPE = NULL,
AND ITI_ITEMCD = #{r_itiitemcd}

mapper.select:
SELECT ITI.*
FROM ITEMINFO ITI

	AND ITI_ITEMCD = #{r_itiitemcd}
	AND ITI_PALLET = #{r_itipallet}
	AND ITI_FILE1 = #{r_itifile1}
	AND ITI_FILE1TYPE = #{r_itifile1type}
	AND ITI_FILE2 = #{r_itifile2}
	AND ITI_FILE2TYPE = #{r_itifile2type}
	AND ITI_FILE3 = #{r_itifile3}
	AND ITI_FILE3TYPE = #{r_itifile3type}
	AND ITI_FILE4 = #{r_itifile4}
	AND ITI_FILE4TYPE = #{r_itifile4type}
	AND ITI_FILE5 = #{r_itifile5}
	AND ITI_FILE5TYPE = #{r_itifile5type}

mapper.insert:
INSERT INTO ITEMRECOMMEND(
	ITR_ITEMCD
	, ITR_RECITEMCD
	, ITR_SORT
)VALUES(
	#{m_itritemcd}
	, #{m_itrrecitemcd}
	, #{m_itrsort}
)

mapper.select:
SELECT * FROM (SELECT 
		ROW_NUMBER() OVER(

			ORDER BY ${r_orderby}
			ORDER BY (SELECT 1)

		) AS ROWNUM
		, XX.* FROM (

SELECT ITR.*, IT.*, ITI.*
	, (SELECT COUNT(*) FROM ITEMRECOMMEND WHERE ITR_ITEMCD = IT.ITEM_CD) AS RECOMMEND_ITEM_COUNT
FROM ITEMRECOMMEND ITR
	LEFT JOIN O_ITEM_NEW IT ON ITR.ITR_RECITEMCD = IT.ITEM_CD
	LEFT JOIN ITEMINFO ITI ON ITR.ITR_RECITEMCD = ITI.ITI_ITEMCD

	AND ITR_ITEMCD = #{r_itritemcd}
	AND ITEM_CD = #{r_itemcd}



) XX ) S


		ROWNUM BETWEEN #{r_startrow} AND #{r_endrow}

mapper.insert:
INSERT INTO ITEMRECOMMEND(
	ITS_ITEMCD
	, ITS_TEXT
	, ITS_TYPE
)VALUES(
	#{m_itsitemcd}
	, #{m_itstext}
	, #{m_itstype}
)

MERGE INTO ITEMBOOKMARK
USING (SELECT 1 AS dual) AS D ON (ITB_ITEMCD = #{m_itbitemcd} AND ITB_USERID = #{m_itbuserid})
WHEN MATCHED THEN 
	UPDATE SET 
		ITB_SORT = 
			#{m_itbsort}
			(SELECT ISNULL(MAX(ITB_SORT), 0)+1 FROM ITEMBOOKMARK WHERE ITB_USERID = #{m_itbuserid})

		, ITB_INID = #{m_itbinid}

		, ITB_INDATE = GETDATE()
WHEN NOT MATCHED THEN
	INSERT (
		ITB_ITEMCD
		, ITB_USERID
		, ITB_SORT
		, ITB_INID
		, ITB_INDATE
	)VALUES(
		#{m_itbitemcd}
		, #{m_itbuserid}
		, 
			#{m_itbsort}
			(SELECT ISNULL(MAX(ITB_SORT), 0)+1 FROM ITEMBOOKMARK WHERE ITB_USERID = #{m_itbuserid})

		, #{m_itbinid}

		, GETDATE()
	) ;

mapper.select:
SELECT * FROM ITEMBOOKMARK

	AND ITB_USERID = #{r_itbuserid}
	AND ITB_ITEMCD = #{r_itbitemcd}

		AND ITB_ITEMCD NOT IN #{itb_itemcd}

mapper.delete:
DELETE FROM ITEMBOOKMARK

	AND ITB_USERID = #{r_itbuserid}

		AND ITB_ITEMCD IN #{itb_itemcd}

mapper.update:
