mapper.update:
UPDATE O_CUSTOMER CU
CUST_CD = #{m_custcd}, CUST_NM = #{m_custnm}, SALESREP_CD = #{m_salesrepcd}, SALESREP_NM = #{m_salesrepnm}, TEAM_CD = #{m_teamcd}, TEAM_NM = #{m_teamnm}, TAX_ID = #{m_taxid}, MAILING_NM = #{m_mailingnm}, ADD1 = #{m_add1}, ADD2 = #{m_add2}, ADD3 = #{m_add3}, ADD4 = #{m_add4}, ZIP_CD = #{m_zipcd}, BUILDING_TY = #{m_buildingty}, BUILDING_NM = #{m_buildingnm}, BUSINESS_TY = #{m_businessty}, BUSINESS_NM = #{m_businessnm}, DUMMY = #{m_dummy}, MOID = #{m_moid}, MODATE = SYSDATE
AND CUST_CD = #{r_custcd}

UPDATE O_CUSTOMER CU
CUST_CD = #{m_custcd}, CUST_NM = #{m_custnm}, SALESREP_CD = #{m_salesrepcd}, SALESREP_NM = #{m_salesrepnm}, TEAM_CD = #{m_teamcd}, TEAM_NM = #{m_teamnm}, TAX_ID = #{m_taxid}, MAILING_NM = #{m_mailingnm}, ADD1 = #{m_add1}, ADD2 = #{m_add2}, ADD3 = #{m_add3}, ADD4 = #{m_add4}, ZIP_CD = #{m_zipcd}, BUILDING_TY = #{m_buildingty}, BUILDING_NM = #{m_buildingnm}, BUSINESS_TY = #{m_businessty}, BUSINESS_NM = #{m_businessnm}, DUMMY = #{m_dummy}, MOID = #{m_moid}, MODATE = SYSDATE
AND CUST_CD IN #{custcd}

mapper.select:
		SELECT 
			CU.*, US.USER_NM, US.AUTHORITY, US.USER_FILE, US.CELL_NO, US.USER_POSITION
			, (SELECT COUNT(*) FROM O_USER WHERE CUST_CD = CU.CUST_CD AND AUTHORITY = 'CO') AS CUSTOMER_USER_CNT
 			, (SELECT COUNT(*) FROM O_USER WHERE CUST_CD = CU.CUST_CD AND AUTHORITY = 'CT') AS SHIPTO_USER_CNT
			, (SELECT COUNT(*) FROM O_SHIPTO WHERE CUST_CD = CU.CUST_CD) AS SHIPTO_CNT
			, US2.USER_NM AS CSUSER_NM
			, US2.TEL_NO AS CSUSER_TEL
			, US2.CELL_NO AS CSUSER_CELL
			, US2.USER_FILE AS CSUSER_FILE
			, US2.USER_POSITION AS CSUSER_POSITION
		FROM O_CUSTOMER CU
			LEFT JOIN O_USER US ON CU.SALESREP_CD = US.USERID
			LEFT JOIN O_CSSALESMAP CSM ON CU.SALESREP_CD = CSM.SALESUSERID AND CSM.FIXEDYN='Y' 
    		LEFT JOIN O_USER US2 ON CSM.CSUSERID = US2.USERID

			AND CU.CUST_CD = #{r_custcd}
			AND CU.CUST_CD LIKE '%' || #{rl_custcd} || '%'
			AND CU.CUST_NM = #{r_custnm}
			AND CU.CUST_NM LIKE '%' || #{rl_custnm} || '%'
			AND SALESREP_CD = #{r_salesrepcd}
			AND SALESREP_CD LIKE '%' || #{rl_salesrepcd} || '%'
			AND SALESREP_NM = #{r_salesrepnm}
			AND SALESREP_NM LIKE '%' || #{rl_salesrepnm} || '%'
			AND TEAM_CD = #{r_teamcd}
			AND TEAM_CD LIKE '%' || #{rl_teamcd} || '%'
			AND TEAM_NM = #{r_teamnm}
			AND TEAM_NM LIKE '%' || #{rl_teamnm} || '%'
			AND TAX_ID = #{r_taxid}
			AND TAX_ID LIKE '%' || #{rl_taxid} || '%'
			AND MAILING_NM = #{r_mailingnm}
			AND MAILING_NM LIKE '%' || #{rl_mailingnm} || '%'
			AND ADD1 = #{r_add1}
			AND ADD1 LIKE '%' || #{rl_add1} || '%'
			AND ADD2 = #{r_add2}
			AND ADD2 LIKE '%' || #{rl_add2} || '%'
			AND ADD3 = #{r_add3}
			AND ADD3 LIKE '%' || #{rl_add3} || '%'
			AND ADD4 = #{r_add4}
			AND ADD4 LIKE '%' || #{rl_add4} || '%'
			AND ZIP_CD = #{r_zipcd}
			AND ZIP_CD LIKE '%' || #{rl_zipcd} || '%'
			AND BUILDING_TY = #{r_buildingty}
			AND BUILDING_TY LIKE '%' || #{rl_buildingty} || '%'
			AND BUILDING_NM = #{r_buildingnm}
			AND BUILDING_NM LIKE '%' || #{rl_buildingnm} || '%'
			AND BUSINESS_TY = #{r_businessty}
			AND BUSINESS_TY LIKE '%' || #{rl_businessty} || '%'
			AND BUSINESS_NM = #{r_businessnm}
			AND BUSINESS_NM LIKE '%' || #{rl_businessnm} || '%'
			AND DUMMY = #{r_dummy}
			AND DUMMY LIKE '%' || #{rl_dummy} || '%'

				AND CUST_CD IN #{custcd}







 					AND CU.SALESREP_CD IN (SELECT SALESUSERID FROM O_CSSALESMAP WHERE CSUSERID = #{r_adminuserid})



 						AND CU.SALESREP_CD IN (SELECT USERID FROM O_USER WHERE USER_CATE2 = #{r_adminuserid})


 						AND CU.SALESREP_CD IN (SELECT USERID FROM O_USER WHERE USER_CATE3 = #{r_adminuserid})


 						AND CU.SALESREP_CD = #{r_adminuserid}

		SELECT COUNT(*) 
		FROM O_CUSTOMER CU
			LEFT JOIN O_USER US ON CU.SALESREP_CD = US.USERID

			AND CUST_CD = #{r_custcd}
			AND CU.CUST_CD LIKE '%' || #{rl_custcd} || '%'
			AND CUST_NM = #{r_custnm}
			AND CUST_NM LIKE '%' || #{rl_custnm} || '%'
			AND SALESREP_CD = #{r_salesrepcd}
			AND SALESREP_CD LIKE '%' || #{rl_salesrepcd} || '%'
			AND SALESREP_NM = #{r_salesrepnm}
			AND SALESREP_NM LIKE '%' || #{rl_salesrepnm} || '%'
			AND US.USER_NM LIKE '%' || #{rl_salesrepnm2} || '%'
			AND TEAM_CD = #{r_teamcd}
			AND TEAM_CD LIKE '%' || #{rl_teamcd} || '%'
			AND TEAM_NM = #{r_teamnm}
			AND TEAM_NM LIKE '%' || #{rl_teamnm} || '%'
			AND TAX_ID = #{r_taxid}
			AND TAX_ID LIKE '%' || #{rl_taxid} || '%'
			AND MAILING_NM = #{r_mailingnm}
			AND MAILING_NM LIKE '%' || #{rl_mailingnm} || '%'
			AND ADD1 = #{r_add1}
			AND ADD1 LIKE '%' || #{rl_add1} || '%'
			AND ADD2 = #{r_add2}
			AND ADD2 LIKE '%' || #{rl_add2} || '%'
			AND ADD3 = #{r_add3}
			AND ADD3 LIKE '%' || #{rl_add3} || '%'
			AND ADD4 = #{r_add4}
			AND ADD4 LIKE '%' || #{rl_add4} || '%'
			AND ZIP_CD = #{r_zipcd}
			AND ZIP_CD LIKE '%' || #{rl_zipcd} || '%'
			AND BUILDING_TY = #{r_buildingty}
			AND BUILDING_TY LIKE '%' || #{rl_buildingty} || '%'
			AND BUILDING_NM = #{r_buildingnm}
			AND BUILDING_NM LIKE '%' || #{rl_buildingnm} || '%'
			AND BUSINESS_TY = #{r_businessty}
			AND BUSINESS_TY LIKE '%' || #{rl_businessty} || '%'
			AND BUSINESS_NM = #{r_businessnm}
			AND BUSINESS_NM LIKE '%' || #{rl_businessnm} || '%'
			AND DUMMY = #{r_dummy}
			AND DUMMY LIKE '%' || #{rl_dummy} || '%'

				AND CUST_CD IN #{custcd}

			AND SALESREP_CD = #{r_salesrepcdyn}
			AND SALESREP_CD != #{rn_salesrepcdyn}






 					AND CU.SALESREP_CD IN (SELECT SALESUSERID FROM O_CSSALESMAP WHERE CSUSERID = #{r_adminuserid})



 						AND CU.SALESREP_CD IN (SELECT USERID FROM O_USER WHERE USER_CATE2 = #{r_adminuserid})


 						AND CU.SALESREP_CD IN (SELECT USERID FROM O_USER WHERE USER_CATE3 = #{r_adminuserid})


 						AND CU.SALESREP_CD = #{r_adminuserid}

		SELECT * FROM (SELECT ROWNUM RR, XX.* FROM (

		SELECT CU.*, SUS.USER_NM AS SALESREP_NM2, SUS.AUTHORITY
			, (SELECT COUNT(*) FROM O_USER WHERE CUST_CD = CU.CUST_CD AND AUTHORITY = 'CO') AS CUSTOMER_USER_CNT
 			, (SELECT COUNT(*) FROM O_USER WHERE CUST_CD = CU.CUST_CD AND AUTHORITY = 'CT') AS SHIPTO_USER_CNT
			, (SELECT COUNT(*) FROM O_SHIPTO WHERE CUST_CD = CU.CUST_CD) AS SHIPTO_CNT
			, (SELECT USER_EMAIL FROM O_USER WHERE USERID = CU.CUST_CD) AS CUST_MAIN_EMAIL
		FROM O_CUSTOMER CU
			LEFT JOIN O_USER SUS ON CU.SALESREP_CD = SUS.USERID

			AND CUST_CD = #{r_custcd}
			AND CU.CUST_CD LIKE '%' || #{rl_custcd} || '%'
			AND CUST_NM = #{r_custnm}
			AND CUST_NM LIKE '%' || #{rl_custnm} || '%'
			AND SALESREP_CD = #{r_salesrepcd}
			AND SALESREP_CD LIKE '%' || #{rl_salesrepcd} || '%'
			AND SALESREP_NM = #{r_salesrepnm}
			AND SALESREP_NM LIKE '%' || #{rl_salesrepnm} || '%'
			AND SUS.USER_NM LIKE '%' || #{rl_salesrepnm2} || '%'
			AND TEAM_CD = #{r_teamcd}
			AND TEAM_CD LIKE '%' || #{rl_teamcd} || '%'
			AND TEAM_NM = #{r_teamnm}
			AND TEAM_NM LIKE '%' || #{rl_teamnm} || '%'
			AND TAX_ID = #{r_taxid}
			AND TAX_ID LIKE '%' || #{rl_taxid} || '%'
			AND MAILING_NM = #{r_mailingnm}
			AND MAILING_NM LIKE '%' || #{rl_mailingnm} || '%'
			AND ADD1 = #{r_add1}
			AND ADD1 LIKE '%' || #{rl_add1} || '%'
			AND ADD2 = #{r_add2}
			AND ADD2 LIKE '%' || #{rl_add2} || '%'
			AND ADD3 = #{r_add3}
			AND ADD3 LIKE '%' || #{rl_add3} || '%'
			AND ADD4 = #{r_add4}
			AND ADD4 LIKE '%' || #{rl_add4} || '%'
			AND ZIP_CD = #{r_zipcd}
			AND ZIP_CD LIKE '%' || #{rl_zipcd} || '%'
			AND BUILDING_TY = #{r_buildingty}
			AND BUILDING_TY LIKE '%' || #{rl_buildingty} || '%'
			AND BUILDING_NM = #{r_buildingnm}
			AND BUILDING_NM LIKE '%' || #{rl_buildingnm} || '%'
			AND BUSINESS_TY = #{r_businessty}
			AND BUSINESS_TY LIKE '%' || #{rl_businessty} || '%'
			AND BUSINESS_NM = #{r_businessnm}
			AND BUSINESS_NM LIKE '%' || #{rl_businessnm} || '%'
			AND DUMMY = #{r_dummy}
			AND DUMMY LIKE '%' || #{rl_dummy} || '%'

				AND CUST_CD IN #{custcd}

			AND SALESREP_CD = #{r_salesrepcdyn}
			AND SALESREP_CD != #{rn_salesrepcdyn}






 					AND CU.SALESREP_CD IN (SELECT SALESUSERID FROM O_CSSALESMAP WHERE CSUSERID = #{r_adminuserid})



 						AND CU.SALESREP_CD IN (SELECT USERID FROM O_USER WHERE USER_CATE2 = #{r_adminuserid})


 						AND CU.SALESREP_CD IN (SELECT USERID FROM O_USER WHERE USER_CATE3 = #{r_adminuserid})


 						AND CU.SALESREP_CD = #{r_adminuserid}








		ORDER BY ${r_orderby}
		) XX 

			ROWNUM <= #{r_endrow}		
		 )

			RR >= #{r_startrow}

SELECT VS.* 
FROM V_SUPPLIER VS

	AND VS.CUST_CD = #{r_custcd}
