mapper.select:
SELECT COUNT(*) FROM O_POSTALCODE_SAP S

	AND S.ZIP_CD LIKE '%' || #{r_zipcd} || '%'

		AND S.USE_F IN #{use_flag}

SELECT S.ZIP_CD , S.ADDR , S.REF , 'EDIT' AS PROC_TYPE , S.USE_F , S.INSERT_DT FROM O_POSTALCODE_SAP S
AND S.ZIP_CD = #{r_zipcode}
AND S.USE_F IN #{use_flag}

SELECT * FROM (SELECT ROWNUM RR, XX.* FROM (
	SELECT S.ZIP_CD
		, S.ADDR
		, S.REF
		, 'EDIT' AS PROC_TYPE
		, S.USE_F
		, S.INSERT_DT
	FROM O_POSTALCODE_SAP S

		AND S.ZIP_CD LIKE '%' || #{r_zipcode} || '%'

			AND S.USE_F IN #{use_flag}


	ORDER BY S.ZIP_CD
) XX 

	ROWNUM <= #{r_endrow}		
 )

	RR >= #{r_startrow}

mapper.insert:
INSERT INTO O_POSTALCODE_SAP (
	ZIP_CD
	, ADDR
	, USE_F
	, INSERT_DT
	, REF
	, USERID
) VALUES (
	#{r_zipcd}
	, #{r_addr}
	, #{r_usef}
	, TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
	, #{r_ref}
	, #{r_userid}
)

mapper.update:
UPDATE O_POSTALCODE_SAP
ZIP_CD = #{r_zipcd}, ADDR = #{r_addr}, USE_F = #{r_usef}, REF = #{r_ref}, USERID = #{r_userid}, INSERT_DT = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
AND ZIP_CD = #{r_zipcd}
